};

// Country code to image file mapping
export const countryImageFlags = {
  // Flag images available in the images/flags folder
  'US': 'us.png',
  'GB': 'gb.png',
  'AU': 'au.png',
  'AR': 'ar.png',
  'AT': 'at.png',
  'AD': 'ad.png',
};

export function getCountryCode(countryName) {
  console.log(`Getting country code for: "${countryName}"`);
  
  // Simplified mapping - only countries with flag images
  const countryCodeMap = {
    'United States': 'US',
    'United Kingdom': 'GB', 
    'Australia': 'AU',
    'Argentina': 'AR',
    'Austria': 'AT',
    'Andorra': 'AD'
  };

  const code = countryCodeMap[countryName];
  if (!code) {
    console.warn(`No flag mapping found for country: "${countryName}". Using ðŸŒ as default.`);
    return 'UN';
  }
  console.log(`Found country code: ${code} for "${countryName}"`);
  return code;
}

export function getCountryFlag(countryName) {
  const countryCode = getCountryCode(countryName);
  return countryFlags[countryCode] || 'ðŸŒ';
}

// New function to get country flag image with fallback to emoji
export function getCountryFlagImage(countryName) {
  const countryCode = getCountryCode(countryName);

  // Check if we have a custom image for this country
  if (countryImageFlags[countryCode]) {
    // Use a simpler path - just the filename
    const imagePath = `/images/flags/${countryImageFlags[countryCode]}`;
    console.log(`Loading flag image for ${countryName} (${countryCode}): ${imagePath}`);
    
    // Test if the image exists
    const img = new Image();
    img.onload = () => {
      console.log(`âœ… Image loaded successfully: ${imagePath}`);
    };
    img.onerror = () => {
      console.error(`âŒ Failed to load image: ${imagePath}`);
    };
    img.src = imagePath;
    
    return {
      type: 'image',
      src: imagePath,
      alt: `${countryName} flag`
    };
  }

  // Fallback to emoji flag
  console.log(`No image found for ${countryName} (${countryCode}), using emoji: ${countryFlags[countryCode] || 'ðŸŒ'}`);
  return {
    type: 'emoji',
    emoji: countryFlags[countryCode] || 'ðŸŒ',
    alt: `${countryName} flag`
  };
}

// Helper function to check if an image exists
export function checkImageExists(imagePath) {
  return new Promise((resolve) => {
    const img = new Image();
    img.onload = () => resolve(true);
    img.onerror = () => resolve(false);
    img.src = imagePath;
  });
}

export function formatNumber(num) {
  if (num === null || num === undefined) return 'N/A';
  return new Intl.NumberFormat().format(num);
}
